import {React, useState, useEffect} from 'react';
import '../styles/config.css'
import {ReactComponent as ConfigIcon} from '../images/config.svg';
import {ReactComponent as CrossIcon} from '../images/cross.svg';
import rgbHex from 'rgb-hex';

export default function CongifMenu(){

  const[backgroundColor,SetbackgroundColor] = useState(null)

  const[shift, Setshift] = useState(true);


  //fondo basico
  useEffect(() => {

    SetbackgroundColor('#' + rgbHex(window.getComputedStyle(document.body).getPropertyValue('background-color')))

  },[])

  function configHandler(e){
    Setshift(!shift);
    console.log(backgroundColor);


  }

  return(
    <div className='configBox'>
      <ConfigIcon className='icon' onClick={configHandler}
      style={{display: shift ? 'block' : 'none'}} ></ConfigIcon>
      <Config shift={shift} configHandler={configHandler} backgroundColor={backgroundColor}></Config>
    </div>
  );
};

export function Config(props){
  // mejorar la estetica de la cruz, user una que matchee mas con el gear
  // hacer los manejadores de eventos en App

  return(

    <form style={{display: props.shift ? 'none' : 'block'}}>

      <div className='configForm'>
        <CrossIcon className='icon' onClick={props.configHandler}></CrossIcon>
        <div>
          <label>Fuente</label>
          <input id='font' type="text"  placeholder="monospace" />
        </div>
        <div>
          <label>Tama√±o fuente</label>
          <input type="" id='font-size' placeholder='10'  />
        </div>
        <div>
          <label>Color</label>
          <input type="color"  />
        </div>
        <div>
          <label>Fondo</label>
          <input type="color" defaultValue='#ffffff' />
        </div>
      </div>
    </form>
  );
};
