import { useEffect} from 'react';

// Identificar los usuarios
export default function useIdentificator(Setlogin, Settxt) {

  // solo al montar
  useEffect(()=> {
    Settxt('cacuno')
    const user = localStorage.getItem('username');
    if (user != null ){
      Setlogin(false);
      getTxt(user, Settxt);

    }
    else {
        Setlogin(true);
    };
  },[]);
  // leer localStorage
  //chequear si el usuario existe
  //si no existe crearlo y guardarlo
  //si existe exhibir datos con una funcion


};

export async function getTxt (user, Settxt){

  const requestOptions = {
    method: 'GET',
  };

  fetch(`api/${user}`, requestOptions).then(response => response.json()).then(data => Settxt(data.text))


}
