import {React, useState, useEffect} from 'react';
import '../styles/config.css'
import {ReactComponent as ConfigIcon} from '../images/config.svg';
import {ReactComponent as CrossIcon} from '../images/cross.svg';
import rgbHex from 'rgb-hex';

export default function CongifMenu(){

  const[fontSize,SetfontSize] = useState(null)
  const[font,Setfont] = useState(null)
  const[color,Setcolor] = useState(null)
  const[backgroundColor,SetbackgroundColor] = useState(null);

  const[shift, Setshift] = useState(true);

  // config setter
  useEffect(()=>{

    const configs = ['color', 'backgroundColor', 'font', 'fontSize']

    // puede parecer repetitivo pero es mas seguro que estar usando plantillas literales con eval

    configs.map(a => {
      const configLocal = localStorage.getItem(a);
      if (configLocal != null){

      switch (a) {
        case 'color':
          Setcolor(configLocal)
          break;
        case 'backgroundColor':
          SetbackgroundColor(configLocal)
          break;
        case 'font':
          Setfont(configLocal)
          break;
        case 'fontSize':
          SetfontSize(configLocal)
          break;


      }
    }else{

      switch (a) {
        case 'color':
          Setcolor('#' + rgbHex(window.getComputedStyle(document.body).getPropertyValue('color')))
          break;
        case 'backgroundColor':
          SetbackgroundColor('#' + rgbHex(window.getComputedStyle(document.body).getPropertyValue('background-color')))
          break;
        case 'font':
          Setfont(window.getComputedStyle(document.body).getPropertyValue('font-family'))
          break;
        case 'fontSize':
          SetfontSize(window.getComputedStyle(document.body).getPropertyValue('font-size'))
          break;


    }
  }
})

  },[])

  function configMenuHandler(e){
    Setshift(!shift);
  }


  // Seria mejor pasar las configuraciones en un Map o objeto para sacarle rigidez
  return(
    <div className='configBox'>
      <ConfigIcon className='icon' onClick={configMenuHandler}
      style={{display: shift ? 'block' : 'none'}} ></ConfigIcon>
      <Config shift={shift} configMenuHandler={configMenuHandler} backgroundColor={backgroundColor} placeholders={[font,fontSize,color,backgroundColor]}></Config>
    </div>
  );
};

export function Config(props){

  // hacer los manejadores de eventos en Ap

  return(

    <form style={{display: props.shift ? 'none' : 'block'}}>

      <div className='configForm'>
        <CrossIcon className='icon' onClick={props.configMenuHandler}></CrossIcon>
        <div>
          <label>Fuente</label>
          <input id='font' type="text"  placeholder={props.placeholders[0]} />
        </div>
        <div>
          <label>Tama√±o fuente</label>
          <input type="" id='font-size' placeholder={props.placeholders[1]}  />
        </div>
        <div>
          <label>Color</label>
          <input type="color" defaultValue={props.placeholders[2]} key={`${Math.floor((Math.random() * 1000))}-min`}  />
        </div>
        <div>
          <label>Fondo</label>
          <input type="color" defaultValue={props.placeholders[3]} key={`${Math.floor((Math.random() * 1000))}-min`} />
        </div>
      </div>
    </form>
  );
};
