import React, {useState} from 'react';
import '../styles/modal.css';

//Tener las funciones en otro archivo me facilita el testing y la reutiizacion
function ModalLogin(props) {

  const[username,Setusername] = useState('');

  function handleChange(e) {
    Setusername(e.target.value);
  };

  function handleLogin(e){
    e.preventDefault();
    try{

      const status =  async (username) => {
        const requestOptions = {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({user:user,txt: ''})
        };

        const rawResponse = await fetch("/api", requestOptions)


        return rawResponse.status
      }
      console.log(status);
      if(status == 403){

        throw new Error('User Existent')
      }
      localStorage.setItem('username', username);

    }catch(e){
      console.log('e');
    }
    props.Setlogin(false);

  };


  return(
    <div className='cont'>
      <div className='modal'>
          <form className='modalContent' onSubmit={handleLogin} name='form'>
            <input type='text' autoFocus placeholder='No te conozco... me decis quien sos?' onChange={handleChange}/>
          </form>
        </div>
    </div>

  );
};


export default ModalLogin;
