import {React, useState, useEffect} from 'react';
import '../styles/config.css'
import {ReactComponent as ConfigIcon} from '../images/config.svg';
import {ReactComponent as CrossIcon} from '../images/cross.svg';
import rgbHex from 'rgb-hex';

export default function CongifMenu(){

  const[fontSize,SetfontSize] = useState(null)
  const[font,Setfont] = useState(null)
  const[color,Setcolor] = useState(null)
  const[backgroundColor,SetbackgroundColor] = useState(null);

  const[shift, Setshift] = useState(true);

  //buscar en localStorage y si no leer los estilos default (puede tener algun modo oscuro)
  useEffect(()=>{

    const configSet = (config,data) => {
      const setter = Function(`Set${config}(${data})`)
      return setter
    }

    const configs = new Map([['color','color'],['backgroundColor','background-color'],['font','font-family'],['fontSize','font-size']])

    configs.forEach((config, style) => {
      const configLocal = localStorage.getItem(config);
      configSet(config,configLocal)()
      if (configLocal =! null) {
        configSet(config,configLocal)()
      }else if (config == 'color' || config == 'backgroundColor'){
        const bodyColor = '#' + rgbHex(window.getComputedStyle(document.body).getPropertyValue(style))

        configSet(config, bodyColor)();
      } else{
        const bodyFont = window.getComputedStyle(document.body).getPropertyValue(style);
        configSet(config,bodyFont)();
      }
    });


    /*
    configs.map(config => {
      const configLocal = localStorage.getItem(config);
      if (configLocal != null){

        eval(`Set${config}('${configLocal}')`)
        // mira en el body
        // bucar manera de en el caso de agregar mas opciones sea facil y menos repetitivo...
      }else if (config == 'backgroundColor'){
        //eval(`Set${config}('#' + rgbHex(window.getComputedStyle(document.body).getPropertyValue('background-color')))`)
      }else if (config == 'color') {
        //eval(`Set${config}('#' + rgbHex(window.getComputedStyle(document.body).getPropertyValue('color')))`)
      }else if (config == 'font'){
        eval(`Set${config}(window.getComputedStyle(document.body).getPropertyValue('font-family'))`)
      }else{
        eval(`Set${config}(window.getComputedStyle(document.body).getPropertyValue('font-size'))`)

      }



    })*/
  },[])



  function configHandler(e){
    Setshift(!shift);
    console.log(backgroundColor);


  }

  return(
    <div className='configBox'>
      <ConfigIcon className='icon' onClick={configHandler}
      style={{display: shift ? 'block' : 'none'}} ></ConfigIcon>
      <Config shift={shift} configHandler={configHandler} backgroundColor={backgroundColor} placeholders={[font,fontSize,color,backgroundColor]}></Config>
    </div>
  );
};

export function Config(props){
  // mejorar la estetica de la cruz, user una que matchee mas con el gear
  // hacer los manejadores de eventos en Ap


  return(

    <form style={{display: props.shift ? 'none' : 'block'}} onBlur={console.log('as')}>

      <div className='configForm'>
        <CrossIcon className='icon' onClick={props.configHandler}></CrossIcon>
        <div>
          <label>Fuente</label>
          <input id='font' type="text"  placeholder={props.placeholders[0]} />
        </div>
        <div>
          <label>Tama√±o fuente</label>
          <input type="" id='font-size' placeholder={props.placeholders[1]}  />
        </div>
        <div>
          <label>Color</label>
          <input type="color" defaultValue={props.placeholders[2]} key={`${Math.floor((Math.random() * 1000))}-min`}  />
        </div>
        <div>
          <label>Fondo</label>
          <input type="color" defaultValue={props.placeholders[3]} key={`${Math.floor((Math.random() * 1000))}-min`} />
        </div>
      </div>
    </form>
  );
};
